mapper.select:
SELECT COUNT(*)
FROM "CART"
WHERE USER_NO = #{userNo} 
AND ITEM_NO = #{itemNo}
AND SIZE_NO = #{sizeNo}

mapper.delete:
DELETE FROM "CART" 
WHERE USER_NO = ${userNo}
AND ITEM_NO IN (${itemNoList})

mapper.update:
UPDATE "CART" SET
	CART_ITEM_COUNT = CART_ITEM_COUNT + (#{cartItemCount})
WHERE USER_NO = #{userNo}
AND ITEM_NO = #{itemNo}
