mapper.select:
SELECT COUNT(*)
FROM "CART"
WHERE USER_NO = #{userNo} 
AND ITEM_STOCK_NO = (
	SELECT ITEM_STOCK_NO 
	FROM "STOCK" 
	WHERE ITEM_STOCK_NO = #{itemStockNo}
)

mapper.delete:
DELETE FROM "CART" 
WHERE USER_NO = ${userNo}
AND ITEM_STOCK_NO IN

		#{i.itemStockNo}

mapper.update:
UPDATE "CART" SET
	CART_ITEM_COUNT = CART_ITEM_COUNT + (#{cartItemCount})
WHERE USER_NO = #{userNo}
AND ITEM_STOCK_NO = (
	SELECT ITEM_STOCK_NO 
	FROM "STOCK" 
	WHERE ITEM_STOCK_NO = #{itemStockNo}
)

UPDATE "CART" SET
	CART_ITEM_COUNT = CART_ITEM_COUNT + ${cartItemCount}
WHERE USER_NO = #{userNo}
AND ITEM_STOCK_NO = #{itemStockNo}
