mapper.select:
SELECT COUNT(*)
FROM "CART"
WHERE USER_NO = #{userNo} 
AND ITEM_NO = #{itemNo}
AND SIZE_NO = #{sizeNo}

mapper.delete:
DELETE FROM "CART" 
WHERE USER_NO = ${userNo}
AND ITEM_NO IN

		#{i.itemNo}


AND SIZE_NO IN

		#{i.sizeNo}

mapper.update:
UPDATE "CART" SET
	CART_ITEM_COUNT = CART_ITEM_COUNT + (#{cartItemCount})
WHERE USER_NO = #{userNo}
AND ITEM_NO = #{itemNo}

mapper.select:
SELECT ITEM_NAME, ITEM_PRICE, ITEM_BRAND, ITEM_IMG_PATH, SIZE_VAL
FROM "ITEM"
JOIN "STOCK" USING(ITEM_NO)
JOIN "SIZE" USING(SIZE_NO)
WHERE ITEM_NO = ${itemNo}
AND SIZE_NO = ${sizeNo}

mapper.update:
UPDATE "CART" SET
	CART_ITEM_COUNT = CART_ITEM_COUNT + ${cartItemCount}
WHERE USER_NO = ${userNo}
AND ITEM_NO = ${itemNo}
AND SIZE_NO = ${sizeNo}
